{% highlight ruby %}
require 'mathn'
f1 = ->a,b,c{  a - b  / c }
f2 = ->a,b,c{ (a - b) / c }
a, b, c = 40, 32, 2
f1[a,b,c] == f2[a,b,c].!

{% highlight ruby %}
require 'mathn'
f1 = ->a,b,c{  a - b  / c }
f2 = ->a,b,c{ (a - b) / c }
a, b, c = 40, 32, 2
f1[a,b,c] == f2[a,b,c].!

